import React, { Component } from "react";

import arxiv from "arxiv";
import ArticleList from "./ArticleList.js";
import MainNav from "../Shared/MainNav.js";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: []
    };
  }

  componentDidMount() {
    var self = this;
    var search_query = {
      title: "machine learning, data science"
    };

    arxiv.search(search_query, async function(err, results) {
      console.log(
        "Found " + results.items.length + " results out of " + results.total
      );
      var response = await results.items.slice(0, 100);
      console.log(results.items[0].title);
      self.setState({
        articles: response
      });
    });
  }

  render() {
    return (
      <div>
        <MainNav />
      </div>
    );
    // return (
    //   <div className="container">
    //     <ArticleList articles={this.state.articles} />
    //   </div>
    // );
  }
}

export default Home;
