import React, { Component } from "react";
import { Link, BrowserRouter as Router, withRouter } from "react-router-dom";

const refreshPage = () => {
  window.location.reload();
};

const consolidate = array => {
  var output = [];

  array.forEach(function(item) {
    var existing = output.filter(function(v, i) {
      return v.name == item.name;
    });
    if (existing.length) {
      var existingIndex = output.indexOf(existing[0]);
      output[existingIndex].value = output[existingIndex].value.concat(
        item.value
      );
    } else {
      if (typeof item.value == "string") item.value = [item.value];
      output.push(item);
    }
  });
  return output;
};

const AuthorList = props => {
  const authors = props.authors.map((author, i) => {
    return (
      <Router>
        <div className="container" style={{ marginTop: "50px" }} key={i}>
          <ul>
            <li onClick={refreshPage}>
              <Link to={{ pathname: "/article", state: author }}>
                {author.name}
              </Link>
            </li>
          </ul>
        </div>
      </Router>
    );
  });

  return <div className="">{authors}</div>;
};

export default AuthorList;
