import React, { Component } from "react";
import { Link } from "react-router-dom";
import ArticleContent from "../Pages/ArticleContent";

class ArticleList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      summary: []
    };
  }

  render() {
    const articles = props.articles.map(article => {
      return (
        <div className="container bg-light" style={{ marginTop: "50px" }}>
          <Link className="" to="/article">
            <blockquote class="blockquote border border-secondary p2 ">
              <p class="mb-0">{article.title}</p>
            </blockquote>
            <ArticleContent message={article.summary} />
          </Link>
        </div>
      );
    });

    return <div className="">{articles}</div>;
  }
}
export default ArticleList;
