import React, { Component } from "react";
import { Link, BrowserRouter as Router, withRouter } from "react-router-dom";

const refreshPage = () => {
  window.location.reload();
};

const consolidate = array => {
  var output = [];

  array.forEach(function(item) {
    output = output.concat(item.authors);
  });
  console.log("o/p", output);
  return output;
};

const AuthorList = props => {
  consolidate(props.authors);
  const authors = props.authors.map((author, i) => {
    return (
      <Router>
        <div className="container" style={{ marginTop: "50px" }} key={i}>
          <ul>
            <li onClick={refreshPage}>
              <Link to={{ pathname: "/article", state: author }}>
                {author.name}
              </Link>
            </li>
          </ul>
        </div>
      </Router>
    );
  });

  return <div className="">{authors}</div>;
};

export default AuthorList;
