import React from "react";

const Spinner = props => {
  return (
    <div class="d-flex justify-content-center">
      <div class="spinner-border align-items-center mx-auto" role="status">
        <span class="sr-only">
          {props.message}
          <strong>{props.message}</strong>
        </span>
      </div>
    </div>
  );
};

Spinner.defaultProps = {
  message: "Loading..."
};

export default Spinner;
