import React, { Component } from "react";

import arxiv from "arxiv";

import Spinner from "./Spinner.js";

class Authors extends Component {
  constructor(props) {
    super(props);
    this.state = {
      authors: []
    };
  }

  componentDidMount() {
    var self = this;
    var search_query = {
      author: "",
      q: all
    };

    arxiv.search(search_query, async function(err, results) {
      console.log(
        "Found " + results.items.length + " results out of " + results.total
      );

      var response = await results.items.slice(0, 10);
      console.log(response);
      self.setState({
        authors: response
      });
    });
  }
  //console.log(results.items[0].title);
  //console.log(results.items[0].authors);
  //console.log(results.items[0].summary);
  renderContent() {
    if (this.state.authors.length > 1) {
      return <AuthorsList articles={this.state.authors} />;
    }

    return <Spinner message="Loading Articles...Please Wait" />;
  }

  render() {
    return (
      <div className="container">
        <div>
          <MainNav />
        </div>
        <div className="">{this.renderContent()}</div>
      </div>
    );
  }
}

export default Authors;
