import React, { Component } from "react";
import { Link } from "react-router-dom";

class ArticleContent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      authors: []
    };
  }
  componentDidMount() {
    var self = this;
    var search_query = {
      author: "",
      q: "all"
    };

    arxiv.search(search_query, async function(err, results) {
      console.log(
        "Found " + results.items.length + " results out of " + results.total
      );

      var response = await results.items.slice(0, 10);
      console.log(response);
      self.setState({
        authors: response
      });
    });
  }

  render() {
    console.log("props", this.props.location.state);
    return (
      <div className="container bg-light" style={{ marginTop: "50px" }}>
        {this.props.location.state}
      </div>
    );
  }
}
export default ArticleContent;
