import React, { Component } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import AuthorsScore from "../AuthorsArt/AuthorsScore";
import MainNav from "../Shared/MainNav";

const refreshPage = () => {
  window.location.reload();
};

const AuthorArticlesList = props => {
  const authors = props.authorsArticles.map((article, i) => {
    return (
      <Router>
        <div className="container" style={{ marginTop: "50px" }}>
          <ul>
            <li onClick={refreshPage} key={i}>
              {article.title}
            </li>
          </ul>
        </div>
      </Router>
    );
  });

  return (
    <div className="">
      {authors}
      <AuthorsScore articles={authors} />
    </div>
  );
};

export default AuthorArticlesList;
