import React, { Component } from "react";
import { Link } from "react-router-dom";
import ArticleContent from "../Pages/ArticleContent";
import Spinner from "./Spinner.js";

class ArticleList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articleSummary: "",
      index: ""
    };
  }

  componentDidUpdate() {
    this.updateSummary();
  }

  handleClick = (e, i) => {
    e.preventDefault();
    this.setState({ index: i });
  };

  updateSummary = () => {
    if (typeof this.state.index === "number") {
      let num = this.state.index;
      console.log("num", num);
      const data = this.props.articles[num].summary;
      this.setState({ articleSummary: data });
    }
  };

  renderContent() {
    if (this.state.articleSummary.length > 1) {
      return (
        <div>
          <ArticleContent summary={this.state.articleSummary} />
        </div>
      );
    }

    return <Spinner message="Loading ArticleContent...Please Wait" />;
  }

  render() {
    const articles = this.props.articles.map((article, i) => {
      return (
        <div className="container" style={{ marginTop: "50px" }}>
          <ul class="list-group">
            <li
              class="list-group-item"
              key={article.id}
              onClick={event => this.handleClick(event, i)}
            >
              <Link className="" to="/article">
                {article.title}
              </Link>
            </li>
          </ul>
        </div>
      );
    });

    return (
      <div className="">
        {articles}
        <div className="">{this.renderContent()}</div>
      </div>
    );
  }
}
export default ArticleList;
