import React, { Component } from "react";
import { Link } from "react-router-dom";
import ArticleContent from "../Pages/ArticleContent";

class ArticleList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      summary: [],
      index: null
    };
  }

  setSummary = () => {
    console.log("here");
    const summary = this.article.summary;

    // this.setState({ spans });
  };
  handleClick = (e, index) => {
    e.preventDefault();
    this.setState({ index: index });
  };

  updateSummary = () => {
    console.log(this.props.articles[this.state.index].summary);
    if (this.state.index) {
      summary.push(this.props.articles[this.state.index].summary);
    }
  };

  render() {
    const articles = this.props.articles.map((article, i) => {
      return (
        <div className="container" style={{ marginTop: "50px" }}>
          <ul class="list-group">
            <li
              class="list-group-item active"
              onClick={event => this.handleClick(event, i)}
              key={article.id}
            >
              {article.title}
            </li>
          </ul>
        </div>
      );
    });

    return (
      <div className="">
        {articles}
        <ArticleContent summary={this.state.summary} />
      </div>
    );
  }
}
export default ArticleList;
