import React, { Component } from "react";
import { Link } from "react-router-dom";
import arxiv from "arxiv";

import Spinner from "../Shared/Spinner.js";

class AuthorArticles extends Component {
  constructor(props) {
    super(props);
    this.state = {
      authorsArts: []
    };
  }
  componentDidMount() {
    var data = this.props.location.state;
    console.log("authorsname", this.props.location.state);
    var self = this;
    var search_query = {
      author: data
    };

    arxiv.search(search_query, async function(err, results) {
      console.log(
        "Found in authorarticles" +
          results.items.length +
          " results out of " +
          results.total
      );

      var response = await results.items;
      console.log("authors", response);
      self.setState({
        authorArts: response
      });
    });
  }

  render() {
    return (
      <div className="container bg-light" style={{ marginTop: "50px" }}>
        it is here {this.props.location.state}
      </div>
    );
  }
}
export default AuthorArticles;
