import React, { Component } from "react";
import { Link } from "react-router-dom";
import ArticleContent from "../Pages/ArticleContent";

class ArticleList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articleSummary: [],
      index: ""
    };
  }

  handleClick = (e, i) => {
    e.preventDefault();
    this.setState({ index: i });
    this.updateSummary();
  };

  updateSummary = () => {
    if (typeof this.state.index === "number") {
      let num = this.state.index;
      console.log("num", num);
      const data = this.props.articles[num].summary;
      this.setState.articleSummary.push(data);
    }
  };

  renderContent() {
    if (this.state.articleSummary.length > 1) {
      return <ArticleList articles={this.state.articles} />;
    }

    return <Spinner message="Loading ArticleContent...Please Wait" />;
  }

  render() {
    const articles = this.props.articles.map((article, i) => {
      return (
        <div className="container" style={{ marginTop: "50px" }}>
          <ul class="list-group">
            <li
              class="list-group-item active"
              onClick={event => this.handleClick(event, i)}
              key={article.id}
            >
              {article.title}
            </li>
          </ul>
        </div>
      );
    });

    return (
      <div className="">
        {articles}
        <ArticleContent summary={this.state.summary} />
      </div>
    );
  }
}
export default ArticleList;
