import React, { Component } from "react";
import { Link } from "react-router-dom";

const ArticleContent = props => {
  return (
    <div className="container bg-light" style={{ marginTop: "50px" }}>
      <blockquote class="blockquote border border-secondary p2 ">
        <p class="mb-0">{props.summary}</p>
      </blockquote>
      {this.state.props.authors.map(author => (
        <div class="list-group">
          <a href="/" class="list-group-item list-group-item-action active">
            {author}
          </a>
        </div>
      ))}
    </div>
  );

  return <div className="">{articles}</div>;
};
export default ArticleList;
