import React, { Component } from "react";
import { Link, BrowserRouter as Router, withRouter } from "react-router-dom";
import ArticleContent from "../Pages/ArticleContent";
import Spinner from "../Shared/Spinner.js";

class ArticleList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articleSummary: "",
      index: ""
    };
  }

  componentDidMount() {}

  handleClick = (e, i) => {
    e.preventDefault();
    this.setState({ index: i });
    this.updateSummary();
  };

  updateSummary = () => {
    if (typeof this.state.index === "number") {
      let num = this.state.index;
      console.log("num", num);
      const data = this.props.articles[num].summary;
      this.setState({ articleSummary: data });
    }
  };

  refreshPage = () => {
    window.location.reload();
  };

  render() {
    const articles = this.props.articles.map((article, i) => {
      return (
        <Router>
          <div className="container" style={{ marginTop: "50px" }} key={i}>
            <ul>
              <li onClick={refreshPage}>
                <Link to={{ pathname: "/article", state: article.summary }}>
                  {article.title}
                </Link>
              </li>
            </ul>
          </div>
        </Router>
      );
    });

    return (
      <Router>
        <div className="">{articles}</div>
      </Router>
    );
  }
}
export default ArticleList;
