import React, { Component } from "react";
import { Link, BrowserRouter as Router } from "react-router-dom";
import AuthorsScore from "../AuthorsArt/AuthorsScore.js";

const refreshPage = () => {
  window.location.reload();
};

const consolidate = array => {
  var output = [];

  array.forEach(function(item) {
    output = output.concat(item.authors);
  });

  return output;
};

const AuthorList = props => {
  const authorsArr = consolidate(props.authors);
  const authors = authorsArr.map((author, i) => {
    return (
      <Router>
        <div className="container" style={{ marginTop: "50px" }}>
          <ul>
            <li onClick={refreshPage} key={author.id}>
              <Link to={{ pathname: "/author", state: author.name }}>
                {author.name}
              </Link>
            </li>
          </ul>
          <div />
        </div>
      </Router>
    );
  });

  return (
    <div className="">
      {authors}
      <div>
        <AuthorsScore authorsList={authors} />
      </div>
    </div>
  );
};

export default AuthorList;
