import React, { Component } from "react";
import { Link } from "react-router-dom";
import arxiv from "arxiv";

import AuthorList1 from "./AuthorList.js";
import Spinner from "../Shared/Spinner.js";

class ArticleContent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      authors: []
    };
  }
  componentDidMount() {
    var self = this;
    var search_query = {
      author: "all"
    };

    arxiv.search(search_query, async function(err, results) {
      console.log(
        "Found " + results.items.length + " results out of " + results.total
      );

      var response = await results.items;
      console.log("authors", response);
      self.setState({
        authors: response
      });
    });
  }

  renderContent() {
    if (this.state.authors.length > 1) {
      return <AuthorList authors={this.state.authors} />;
    }

    return <Spinner message="Loading Articles...Please Wait" />;
  }

  render() {
    return (
      <div className="container bg-light" style={{ marginTop: "50px" }}>
        {this.props.location.state}
        <div className="">{this.renderContent()}</div>
      </div>
    );
  }
}
export default ArticleContent;
